template<typename T>
void timeit(std::function<void(T)> funcPtr,T arg, std::string_view msg){
    auto start = std::chrono::system_clock::now();
    for (size_t i = 0; i < 10e7; ++i)
    {
        funcPtr(arg);
    }
    auto end = std::chrono::system_clock::now();
    std::chrono::duration<double> diff = end-start;
        std::cout << "Time for string passed by " << msg << " is " << diff.count() << " s\n";
}


int main()
{

    std::string aa = "";
    timeit<std::string>(string_copy,aa,"copy");
    timeit<std::string>(string_a,aa,"const string ref");
    timeit<std::string>(string_vv,aa,"string view");

    return 0;
}